import React, { useState, useEffect } from "react";
import LoginForm from "./components/LoginForm";
import AddPassword from "./components/AddPassword";
import PasswordList from "./components/PasswordList";
import { Keychain } from "./password-manager";
import "./App.css";

function App() {
  const [keychain, setKeychain] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [passwords, setPasswords] = useState([]);

  const handleLogin = async (masterPassword) => {
    try {
      const stored = localStorage.getItem("keychain");
      let kc;

      if (stored) {
        kc = await Keychain.load(masterPassword, stored);
      } else {
        kc = await Keychain.init(masterPassword);
        const [repr] = await kc.dump();
        localStorage.setItem("keychain", repr);
      }

      setKeychain(kc);
      setIsLoggedIn(true);

      // Load all stored passwords
      await loadPasswords(kc);
    } catch (error) {
      alert("Login failed: " + error);
    }
  };

  const loadPasswords = async (kc) => {
    const stored = localStorage.getItem("keychain");
    if (!stored) {
      setPasswords([]);
      return;
    }

    try {
      const parsed = JSON.parse(stored);
      const domainMacs = Object.keys(parsed.kvs || {});
      const loadedPasswords = [];

      // Get all domains from kvs keys (they are HMACs)
      // For now, we'll just display the HMAC as domain since we can't reverse HMAC
      for (const mac of domainMacs) {
        loadedPasswords.push({ domain: mac, password: "••••••••" });
      }

      setPasswords(loadedPasswords);
    } catch (error) {
      console.error("Failed to load passwords:", error);
    }
  };

  const handleAddPassword = async (domain, password) => {
    if (!keychain) return;
    try {
      await keychain.set(domain, password);
      const [repr] = await keychain.dump();
      localStorage.setItem("keychain", repr);
      setPasswords([...passwords, { domain, password: "••••••••" }]);
    } catch (error) {
      alert("Failed to add password: " + error);
    }
  };

  const handleRemovePassword = async (index) => {
    if (!keychain) return;
    try {
      const domainMac = passwords[index].domain;
      await keychain.remove(domainMac);
      const [repr] = await keychain.dump();
      localStorage.setItem("keychain", repr);
      setPasswords(passwords.filter((_, i) => i !== index));
    } catch (error) {
      alert("Failed to remove password: " + error);
    }
  };

  return (
    <div className="App">
      <h1>Secure Password Manager</h1>
      {!isLoggedIn ? (
        <LoginForm onLogin={handleLogin} />
      ) : (
        <div className="container">
          <AddPassword onAdd={handleAddPassword} />
          <PasswordList passwords={passwords} onRemove={handleRemovePassword} />
          <button onClick={() => setIsLoggedIn(false)}>Logout</button>
        </div>
      )}
    </div>
  );
}

export default App;
