import React, { useState, useEffect } from "react";
import LoginForm from "./components/LoginForm";
import AddPassword from "./components/AddPassword";
import PasswordList from "./components/PasswordList";
import { Keychain } from "./password-manager";
import "./App.css";

function App() {
  const [keychain, setKeychain] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [passwords, setPasswords] = useState([]);

  const handleLogin = async (masterPassword) => {
    try {
      const stored = localStorage.getItem("keychain");
      if (stored) {
        const kc = await Keychain.load(masterPassword, stored);
        setKeychain(kc);
        setIsLoggedIn(true);
      } else {
        const kc = await Keychain.init(masterPassword);
        setKeychain(kc);
        setIsLoggedIn(true);
      }
    } catch (error) {
      alert("Login failed: " + error);
    }
  };

  const handleAddPassword = async (domain, password) => {
    if (!keychain) return;
    try {
      await keychain.set(domain, password);
      const [repr] = await keychain.dump();
      localStorage.setItem("keychain", repr);
      setPasswords([...passwords, { domain, password }]);
    } catch (error) {
      alert("Failed to add password: " + error);
    }
  };

  const handleRemovePassword = async (domain) => {
    if (!keychain) return;
    try {
      await keychain.remove(domain);
      const [repr] = await keychain.dump();
      localStorage.setItem("keychain", repr);
      setPasswords(passwords.filter((p) => p.domain !== domain));
    } catch (error) {
      alert("Failed to remove password: " + error);
    }
  };

  return (
    <div className="App">
      <h1>Secure Password Manager</h1>
      {!isLoggedIn ? (
        <LoginForm onLogin={handleLogin} />
      ) : (
        <div className="container">
          <AddPassword onAdd={handleAddPassword} />
          <PasswordList passwords={passwords} onRemove={handleRemovePassword} />
        </div>
      )}
    </div>
  );
}

export default App;
